{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"provenance":[],"authorship_tag":"ABX9TyNh59ejXs0w2Ztrtk4yTvVr"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","execution_count":null,"metadata":{"id":"bEpILPQHMroG"},"outputs":[],"source":["from openai import OpenAI\n","import streamlit as st\n","\n","with st.sidebar:\n","    openai_api_key = st.text_input(\"OpenAI API Key\", key=\"chatbot_api_key\", type=\"password\")\n","    \"[Get an OpenAI API key](https://platform.openai.com/account/api-keys)\"\n","    \"[View the source code](https://github.com/streamlit/llm-examples/blob/main/Chatbot.py)\"\n","    \"[![Open in GitHub Codespaces](https://github.com/codespaces/badge.svg)](https://codespaces.new/streamlit/llm-examples?quickstart=1)\"\n","\n","st.title(\"ðŸ’¬ Chatbot\")\n","st.caption(\"ðŸš€ A streamlit chatbot powered by OpenAI LLM\")\n","if \"messages\" not in st.session_state:\n","    st.session_state[\"messages\"] = [{\"role\": \"assistant\", \"content\": \"How can I help you?\"}]\n","\n","for msg in st.session_state.messages:\n","    st.chat_message(msg[\"role\"]).write(msg[\"content\"])\n","\n","if prompt := st.chat_input():\n","    if not openai_api_key:\n","        st.info(\"Please add your OpenAI API key to continue.\")\n","        st.stop()\n","\n","    client = OpenAI(api_key=openai_api_key)\n","    st.session_state.messages.append({\"role\": \"user\", \"content\": prompt})\n","    st.chat_message(\"user\").write(prompt)\n","    response = client.chat.completions.create(model=\"gpt-3.5-turbo\", messages=st.session_state.messages)\n","    msg = response.choices[0].message.content\n","    st.session_state.messages.append({\"role\": \"assistant\", \"content\": msg})\n","    st.chat_message(\"assistant\").write(msg)"]}]}